window.onload=(()=>{document.body.getBoundingClientRect().height;document.body.addEventListener("scroll",e=>{revealContent(),handleHeaderReadability()})});const revealContent=()=>{const e=[document.getElementById("container-2")];if(document.body.scrollTop>.5*document.body.clientHeight){console.log(Math.ceil(document.body.scrollTop/document.body.clientHeight)-1);const t=e[Math.ceil(document.body.scrollTop/document.body.clientHeight)-1].querySelectorAll("section");for(section of t)if(!section.classList.contains("reveal-content")){const e=document.body.scrollTop%document.body.clientHeight>=section.getBoundingClientRect().height;console.log("should be revealed: ",e),e&&section.classList.add("reveal-content")}}},handleHeaderReadability=()=>{document.body.scrollTop>0?makeHeaderMoreReadable():setDefaultHeaderReadability()},makeHeaderMoreReadable=()=>{document.querySelector("header").classList.add("better-visible")},setDefaultHeaderReadability=()=>{document.querySelector("header").classList.remove("better-visible")};